	//ACTION CREATE
	public function create_action()
	{
		$data = [
			'id' => $this->request->getVar('id'),
			'urusan' => $this->request->getVar('urusan'),
			'output_outcome' => $this->request->getVar('output_outcome'),
			'jenis_urusan' => $this->request->getVar('jenis_urusan'),

