	//UPDATE
	public function update($id)
	{
		$dataFind = $this->Model->find($id);
		if ($dataFind == false) {
			return redirect()->to(base_url('/Urusan'));
		}
		$data = [
			'AttributePage' => $this->PageData,
			'content' => 'Edite Pages',
			'action' => 'urusan/update_action',
			'data' => $this->Model->find($id),
		];
		session()->setFlashdata('message', 'Update Record Success');
		return view('urusan/form_urusan', $data);
	}

	//ACTION UPDATE
	public function update_action()
	{
		$id = $this->request->getVar('id');
		$row = $this->Model->find(['id' => $id]);

		$data = [
			'id' => $this->request->getVar('id'),
			'urusan' => $this->request->getVar('urusan'),
			'output_outcome' => $this->request->getVar('output_outcome'),
			'jenis_urusan' => $this->request->getVar('jenis_urusan'),
		];
		$this->Model->save($data);
		session()->setFlashdata('message', 'Update Record Success');

		return redirect()->to(base_url('urusan'));
	}

	//DELETE
	public function delete($id)
	{
		$row = $this->Model->find(['id' => $id]);
		if ($row) {
			$this->Model->delete($id);
			session()->setFlashdata('message', 'Delete Record Success');
			return redirect()->to(base_url('/urusan'));
		} else {
			session()->setFlashdata('message', 'Record Not Found');
			return redirect()->to(base_url('/urusan'));
		}
	}

	public function getCombo()
	{
		$data = [];
		$db      = db_connect();
		$builder = $db->table('urusan');
		$query = $builder->like('urusan', @$this->request->getVar('q'))
			->select('id, concat(urusan,output_outcome) as text')
			->limit(10)->get();
		$data = $query->getResult();
		echo json_encode($data);
	}


	//RULES
	public function _rules()
